use nyc_taxi_ldw
GO

create or ALTER PROCEDURE silver.usp_silver_trip_data_green
@year VARCHAR(4),
@month VARCHAR(2)
As BEGIN
    Declare @create_sql_stmt NVARCHAR(MAX),
            @drop_sql NVARCHAR(MAX);

    Set @create_sql_stmt = 
    'CREATE EXTERNAL TABLE silver.trip_data_green_' + @year + '_' + @month + '
        WITH (
                LOCATION = ''silver/trip_data_green/year=''' + @year + '/month=' + @month + ',
                DATA_SOURCE = nyc_tax_src,
                FILE_FORMAT = ParquetFileFormat
                ) AS

        SELECT
        [result].filename() as file_name,
        [result].filepath(1) as year,
        [result].filepath(2) as month,
        [result].*
        FROM
            OPENROWSET(
                BULK (''raw/trip_data_green_csv/year=''' + @year + '/month=' + @month + '/*.csv),
                DATA_SOURCE = ''nyc_tax_src'',
                FORMAT = ''CSV'',
                PARSER_VERSION = ''2.0'',
                HEADER_ROW = TRUE
            ) AS [result]' ;

    print(@create_sql_stmt);

    EXEC sp_executesql @create_sql_stmt;

    SET @drop_sql = 'DROP EXTERNAL TABLE silver.trip_data_green_' + @year + '_' + @month + '';

    print(@drop_sql);

    EXEC sp_executesql @drop_sql;

END;
